import React from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Button from 'react-bootstrap/Button';


class App extends React.Component{
  constructor(){
    super()
     this.state ={
       fullName:'Naruto Uzumaki',
       imgSrc:'naruto.jpg',
       bio:'Né le 10 october 1990,il est un ninja est le hokage du village caché de Konoha.',
       profession:'Ninja,Hokage',
       show:1,
       time:0,
       styleContainer:{backgroundColor: "#E0FFFF"}}
     }
updatetime=()=>{
   this.setState(prevState=>{return {time:prevState.time+1} })
}
componentDidMount(){
   setInterval(() => this.updatetime(),1000);
}
handleChange=()=>{
  if(this.state.show){
this.setState({
  styleContainer:{display:"none"},
show:false})
  }else{
this.setState({
  styleContainer:{
backgroundColor: "#E0FFFF"},
  show:true
})
}}
render(){
  return (
  <React.Fragment>
<div className='Card'>
<div style={this.state.styleContainer}>
<h1>{this.state.fullName}</h1>
<img width ='250px' style={{borderRadius:"7px"}} src={this.state.imgSrc}/>
<h3><h3 className='bio'>Bio</h3>{this.state.bio}</h3>
<h3><h3 className='pro'>Profession</h3>{this.state.profession}</h3>
<p>{this.state.time}</p>
</div>
</div>
<div className='btn'>
<button onClick={this.handleChange}>Click</button>
</div>


  </React.Fragment>

  );
  }
  }
export default App;
