import React from 'react';
import './App.css';
class App extends React.Component{
  constructor(){
    super()
     this.state ={
       fullName:'Naruto Uzumaki',
       imgSrc:'naruto.jpg',
       bio:'fdsdf',
       profession:'qdfq',
       show:1,
       time:0,
       styleContainer:{backgroundColor: "white"}}
     }
updatetime=()=>{
   this.setState(prevState=>{return {time:prevState.time+1} })
}
componentDidMount(){
   setInterval(() => this.updatetime(),1000);
}
handleChange=()=>{
  if(this.state.show){
this.setState({
  styleContainer:{display:"none"},
show:false})
  }else{
this.setState({
  styleContainer:{
width: "auto",
  border: "1px solid grey",
backgroundColor: "white"},
  show:true
})
}}
render(){
  return (
  <React.Fragment>
    <div className='Card'>
    <div style={this.state.styleContainer}>
    <h1>{this.state.fullName}</h1>
      <img width ='250px' style={{borderRadius:"50%"}} src={this.state.imgSrc}/>

          <p>{this.state.bio}</p>
            <p>{this.state.profession}</p>
              <h1>{this.state.time}</h1>


    </div>
    </div>
    <div className='btn'>
    <button onClick={this.handleChange}>Click</button>
    </div>
  </React.Fragment>

  );
  }
  }
export default App;
