import React from 'react';
import './App.css';

class App extends React.Component{
  constructor(){
    super()
     this.state ={
       fullName:'Uzumaki Naruto',bio:'Né le 10 October 1990,st un ninja du village caché de Konoha',imgSrc:"df",profession:'Ninja,Hokage',show:1,time:0,
       styleContainer:{backgroundColor: "#ede1f7",border:"1px solid blue"}}
     }
updatetime=()=>{
   this.setState(prevState=>{return {time:prevState.time+1} })
}
componentDidMount(){
   setInterval(() => this.updatetime(),1000);
}
handleChange=()=>{
  if(this.state.show){
this.setState({
  styleContainer:{display:"none"},
show:false})
  }else{
this.setState({
  styleContainer:{
width: "auto",
  border: "1px solid grey",
backgroundColor: "#ede1f7"},
  show:true
})
}}
render(){
  return (
  <React.Fragment>
    <div className='Card'>
    <div style={this.state.styleContainer}>
    <h1>{this.state.time}</h1>
    <p>{this.state.fullName}</p>
    <p>{this.state.bio}</p>
    <p>{this.state.profession}</p>
    </div>
    </div>
    <button onClick={this.handleChange}>Click</button>
  </React.Fragment>
  );
  }
  }
export default App;
