import React from 'react';
import './App.css';


class App extends React.Component{
  constructor(){
    super()
    this.state ={
      fullName:'qdqd',bio:'fdsdf',imgSrc:'df',profession:'qdfq',show:1,
      styleContainer:{backgroundColor: "#ede1f7",border:"1px solid red"}
    }

  }

  handlebtn=()=>{
      if(this.state.show){
        this.setState({
          styleContainer:{display:"none"},
          show:false})
      }else{
        this.setState({
          styleContainer:{
            width: "auto",
            border: "1px solid grey",
            backgroundColor: "#ede1f7"},
          show:true
        })}}

  render(){

  return (
  <React.Fragment>
    <div style={this.state.styleContainer}>
        <h1>some txt</h1>
    </div>
    <button onClick={this.handlebtn}/>
  </React.Fragment>

  );
}
}
export default App;
